import React from "react";
import styled from "styled-components";
import Card from "../components/Card";

const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 16px; /* Espaçamento entre os cartões */
`;

const CardWrapper = styled.div`
  flex-basis: calc(33.33% - 16px); /* 3 colunas em telas maiores */
  margin-bottom: 16px; /* Espaço inferior entre as linhas de cartões */

  @media (max-width: 768px) {
    flex-basis: calc(50% - 16px); /* 2 colunas em telas médias */
  }

  @media (max-width: 480px) {
    flex-basis: calc(100% - 16px); /* 1 coluna em telas menores */
  }
`;

const Home = () => {
  return (
    <Container>
      <CardWrapper>
        <Card />
      </CardWrapper>
      <CardWrapper>
        <Card />
      </CardWrapper>
      {/* Adicione mais CardWrapper para cada cartão */}
    </Container>
  );
};

export default Home;
